import Avatar from "@material-ui/core/Avatar";
import Box from "@material-ui/core/Box";
import Button from "@material-ui/core/Button";
import Checkbox from "@material-ui/core/Checkbox";
import Container from "@material-ui/core/Container";
import CssBaseline from "@material-ui/core/CssBaseline";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import Grid from "@material-ui/core/Grid";
import Link from "@material-ui/core/Link";
import { makeStyles } from "@material-ui/core/styles";
import TextField from "@material-ui/core/TextField";
import Typography from "@material-ui/core/Typography";
import LockOutlinedIcon from "@material-ui/icons/LockOutlined";
import { toast } from "react-toastify";
import PasswordStrengthBar from 'react-password-strength-bar';








import React, { Component } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import img from './img/contact.jpg'


const useStyles = makeStyles((theme) => ({
    paper: {
      marginTop: theme.spacing(8),
      display: "flex",
      flexDirection: "column",
      alignItems: "center",
    },
    avatar: {
      margin: theme.spacing(1),
      backgroundColor: theme.palette.secondary.main,
    },
    form: {
      width: "100%", // Fix IE 11 issue.
      marginTop: theme.spacing(3),
    },
    submit: {
      margin: theme.spacing(3, 0, 2),
    },
  }));



class Contact extends Component {


    constructor(props) {
        super(props);
        const classes = useStyles();
        this.state = {
            username: '',
            email: '',
            query:'',
            redirect: false,
        };
    }


    handleInputChange = e => {
        this.setState({
            [e.target.name]: e.target.value,
        });
    };

    handleSubmit = e => {
        e.preventDefault();

        // console.log("happy")
        const { username , email, query} = this.state;


        const contactus = {
            username,
            email,
            query,
        };

        // console.log(account);



        axios.post('http://localhost:5000/contact', contactus )
            .then((res) => {

                console.log(res.status);
                window.location.href = '/submitted';
            })

            .catch(err => {
                console.error(err.status);
            },{
                withCredentials: true,
                headers: {'Access-Control-Allow-Origin': '*', 'Content-Type': 'application/json'
            }});

        this.setState({ username: '' , email: '' , query: ''})


        document.forms['myform'].reset();


    };




    render() {


        return (
            <div className="container">

                <br />
                <div className="row">

                    <div className="col-md-6">

                    <img src={img} width="100%" height="auto"/>


                    </div>
                    <div className="col-md-4">



                <div className="container">

                        <div className="login-form">
                            <h1>Drop us a Line</h1>
                            <form id="myform" onSubmit={this.handleSubmit}>
                                <input type="text" name="username" placeholder="Username" required onChange={this.handleInputChange}/>
                                    <input type="email" name="email" placeholder="email" required onChange={this.handleInputChange}/>
                                    <input type="text" name="query" placeholder="query" required onChange={this.handleInputChange}/>
                                        <input type="submit"/>

                            </form>
                        </div>
                </div>

                    </div>
                </div>

                <Container component="main" maxWidth="xs">
      <CssBaseline />
      <div className={classes.paper}>
        <Avatar className={classes.avatar}>
          <LockOutlinedIcon />
        </Avatar>
        <Typography component="h1" variant="h5">
          Sign up
        </Typography>
        <form className={classes.form} noValidate>
          <Grid container spacing={2}>
            <Grid item xs={12}>
              <TextField
                autoComplete="name"
                name="name"
                variant="outlined"
                required
                fullWidth
                id="firstName"
                label="Full Name"
                autoFocus
                value={name}
                onChange={(e) => {
                  setName(e.target.value);
                }}
              />
            </Grid>

            <Grid item xs={12}>
              <TextField
                variant="outlined"
                required
                fullWidth
                id="email"
                label="Email Address"
                name="email"
                type="email"
                autoComplete="email"
                value={email}
                onChange={(e) => {
                  setEmail(e.target.value);
                }}
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                variant="outlined"
                required
                fullWidth
                name="password"
                label="Password"
                type="password"
                id="password"
                autoComplete="current-password"
                value={password}
                onChange={(e) => {
                  setPassword(e.target.value);


                }}
              />
            </Grid>
          </Grid>
                  <br></br>
          <PasswordStrengthBar password={password} />

          <Button
            fullWidth
            className={classes.submit}
            variant="contained"
            color="primary"
            onClick={(e) => {
             alert('Hi')
            }}
          >
            Sign Up
          </Button>
          <Grid container justify="flex-end">
            <Grid item>
              <Link href="/login" variant="body2">
                Already have an account? Sign in
              </Link>
            </Grid>
          </Grid>
        </form>
      </div>
      <Box mt={5}>
        <Copyright />
      </Box>
    </Container>
            </div>
        );
    }
}

export default Contact;
