import GenericServices from "./GenericServices";
import jwt_decode from "jwt-decode";
import axios from "axios"
class userServices extends GenericServices {
  login = (userName, password) =>


  // axios.post('http://localhost:5000/auth', {userName, password} )
  //             .then((res) =>{ 
  //                             // this.data = res.data
  //                             console.log(res.status);
  //                             console.log(res.isLoggedin);
  //                 if(res.isLoggedin){

  //                     window.location.href = '/welcome ';
  //                 }});

    new Promise((resolve, reject) => {
      this.post("http://localhost:5000/auth", { userName, password })
        .then((token) => {
          localStorage.setItem("token", token);
          resolve(token);
        })
        .catch((err) => {
          reject(err);
        });
    });

  register = (name, email, password) =>
    new Promise((resolve, reject) => {
      this.post("users/register", { name, email, password })
        .then((res) => {
          resolve(res);
        })
        .catch((err) => {
          reject(err);
        });
    });

  isLoggedin = localStorage.getItem("token") ? true : false;

  logout = () => {
    localStorage.removeItem("token");
    window.location.href = "/";
  };

  getLoggedinfo = () => {
    try {
      let jwt = localStorage.getItem("token");
      var decode = jwt_decode(jwt);
      return decode;
    } catch (error) {
      console.log("error");
    }
  };
  isAdmin = () => {
    if (this.isLoggedIn()) {
      if (this.getLoggedinfo().role === "admin") return true;
      else return false;
    } else return false;
  };
}

let UserServices = new userServices();
export default UserServices;
