                                                            progress: undefined,
                                                        });
                                                    });
                                                }}} >Save</Button>
                                                ) : 
                                                ("")
                                            }  

                                            <Button 
                                                onClick={saveLayer} >
                                                Save Layer
                                            </Button>

                                            <Button 
                                                onClick={getData} >
                                                get data
                                            </Button>

                                            <Button 
                                                onClick={addLayer} >
                                                Add Layer
                                            </Button>
                                            <Button 
                                                id = "selection" 
                                                onClick={() => setTool("selection")}   
                                                aria-label="left aligned">
                                                Selection
                                            </Button>

                                            <Button 
                                                id = "selection" 
                                                onClick={() => setTool("sizes")}   
                                                aria-label="left aligned">
                                                Get Size
                                            </Button>
                                            <Button 
                                                id = "text" 
                                                onClick={() => setTool("text")}>
                                                Text
                                            </Button>
                                            <Button 
                                                id = "resize" 
                                                onClick={() => setTool("resize")}>
                                                Re - Factor
                                            </Button>
                                            <Button 
                                                id = "line" 
                                                onClick={() => setTool("line")}>
                                                Wall
                                            </Button>
                                            <Button 
                                                id = "rectangle" 
                                                onClick={() => setTool("rectangle")}>
                                                Room
                                            </Button>
                                            <Button 
                                                id = "ellipse" 
                                                onClick={() => 
                                                setTool("ellipse")}>
                                                Ellipse Shape Room
                                            </Button>


                                            <Button 
                                                aria-controls="simple-menu" 
                                                aria-haspopup="true" 
                                                onClick={handleClick}>
                                                Door
                                            </Button>

                                                <Menu
                                                    id="simple-menu"
                                                    anchorEl={anchorEl}
                                                    keepMounted
                                                    open={Boolean(anchorEl)}
                                                    onClose={handleClose}
                                                    >

                                                    <MenuItem 
                                                        id = "arcL" 
                                                        onClick={() => {setTool("arcL")
                                                                        setAnchorEl(null);}  }>
                                                        Door Left
                                                    </MenuItem>
                                                    <MenuItem 
                                                        id = "arcR" 
                                                        onClick={() => {setTool("arcR") 
                                                                        setAnchorEl(null);} }>
                                                        Door Right
                                                    </MenuItem>

                                                    <MenuItem 
                                                        onClick={handleClose}>
                                                        Logout
                                                    </MenuItem>

                                                </Menu>


                                            <Button 
                                                id = "circle" 
                                                onClick={() => setTool("circle")}>
                                                circular room
                                            </Button>
                                            <Button 
                                                id = "eraser" 
                                                onClick={() => setTool("eraser")}>
                                                Erase
                                            </Button>
                                            <Button  
                                                onClick={lessItem} >
                                                Change Layer <ExpandLessIcon/> 
                                            </Button>
                                            <Button 
                                                onClick={upItem} >
                                                Change Layer <ExpandMoreIcon/>
                                            </Button>
                                            <Button 
                                            onClick={handleSave} >
                                            Save Local 
                                            </Button>
                                            <Button 
                                            onClick={getJson} >
                                            JSON
                                            </Button>

                                            <Button onClick={()=>{
                                                if(widthh === null)
                                                {
                                                    alert("Please Enter Width and Length First")
                                                }
                                                else{
                                                const canv = document.getElementById("canvas");
                                                const ctx = canv.getContext('2d')
                                                ctx.clearRect(0, 0, canvas.width, canvas.height);
                                                setElements([]);
                                                setMyVar([]);
                                                item = 0;
                                                totalLayer = 0;
                                                }}} >
                                                Clear All
                                            </Button>

                                            <Button 
                                                onClick={scrapMe
                                                }>
                                                Scrap Me
                                            </Button>
                                        </ButtonGroup>

                                    </div>
                                </div>


                                <div
                                    style= {{ marginTop  : "5%" ,
                                    }}>
                                        <h3 style= {{ marginLeft  : "15%"
                                    }}>Your Current Floor is : {item} / {totalLayer}</h3>
                                        <canvas
                                            style = {{
                                                background:"#c0c6c5" , 
                                                border : "5px solid darkslategrey" , 
                                                margin : "5%"}}

                                            width = {canvWidth}
                                            height = {canvHeight}
                                            id = "canvas"
                                            onMouseDown={handleMouseDown}
                                            onMouseUp={handleMouseUp}
                                            onMouseMove={handleMouseMove}

                                            >
                                            Canvas
                                        </canvas>

                                </div>
                            </div>

                            <div>

                                {selectedElement === null ? (

                                    <div className="container">
                                      <InputLabel htmlFor="input-with-icon-adornment">Current selected element is : </InputLabel>
                                        <Input
                                            value  = "No element is selected"
                                            readOnly
                                            startAdornment={
                                                <InputAdornment position="start">
                                                </InputAdornment>
                                                }
                                            />
                                    </div>
                                    ) : 

                                    <div className = "container">
                                        <InputLabel htmlFor="input-with-icon-adornment">Current selected element is : </InputLabel>

                                        <Input

                                            value  =  {selectedElement.type === "rectangle" ? (
                                                "Room"            
                                                ) : 
                                            selectedElement.type === "line" ? (
                                                "Wall"
                                                ) : 
                                            selectedElement.type === "ellipse" ? (
                                                "Ellipse Shape Room"
                                                ) :
                                            selectedElement.type === "arcL" ? (
                                                "Door Left"
                                                ) : 
                                            selectedElement.type === "arcR" ? (
                                                "Door Right"
                                                ) : 
                                            selectedElement.type === "circle" ? (
                                                "Circular Room"
                                                ) : ("")

                                            }  

                                            readOnly
                                            startAdornment={
                                                <InputAdornment position="start">

                                                </InputAdornment>
                                                }
                                        />
                                    </div>

                                }

                                <InputLabel htmlFor="input-with-icon-adornment">Your current Width is : </InputLabel>
                                    <Input
                                        value  = {canvWidth / 15.36 } 
                                        type="number"
                                        step=".01"
                                        readOnly
                                        startAdornment={
                                            <InputAdornment position="start">

                                            </InputAdornment>
                                        }
                                    />


                                <InputLabel htmlFor="input-with-icon-adornment">Your current Length is : </InputLabel>
                                    <Input
                                        value  = {canvHeight / 15.36 } 
                                        type="number"
                                        step=".01"
                                        readOnly
                                        startAdornment={
                                            <InputAdornment position="start">

                                            </InputAdornment>
                                        }
                                    />


                                <div>
                                    <InputLabel htmlFor="input-with-icon-adornment">Element current Width is : </InputLabel>
                                        <Input
                                            value  = {  ElementWidth   / 15.36 } 
                                            type="number"
                                            step=".01"
                                            readOnly
                                            startAdornment={
                                            <InputAdornment position="start">

                                            </InputAdornment>
                                            }
                                        />
                                </div>



                                <div>
                                    <InputLabel htmlFor="input-with-icon-adornment">Element current Length is : </InputLabel>
                                        <Input
                                            value  = {
                                                (   ElementLength   )   /   15.36 }
                                                type="number"
                                                step=".01"
                                                readOnly
                                                startAdornment={
                                                <InputAdornment position="start">

                                                </InputAdornment>
                                                }
                                        />
                                </div>

                            </div>

                            {error ? (

                               <div className="container">
                                   <h3>Values must be valid</h3>

                                </div>
                            ) :  

                            ("")

                            } 
                            {loading ? (

                               <div className="container">

                                   <Spin style={{width:'100%' , marginTop: '20%' , marginBottom:'20%'}} />

                                </div>
                            ) :  

                            ("")

                            }        
                        </div>
                    );
            }

export default App;
