import GenericServices from "./GenericServices";
import jwt_decode from "jwt-decode";
class userServices extends GenericServices {
  login = (userName, password) =>

    new Promise((resolve, reject) => {

      console.log(userName , password);

      this.post("http://localhost:5000/auth", { userName, password })
        .then((token) => {
          localStorage.setItem("token", token);
          resolve(token);

        })
        .catch((err) => {
          reject(err);
        });
    });

  register = (username, email, password , role) =>
    new Promise((resolve, reject) => {


      console.log(username)

      this.post("http://localhost:5000/register", { username, email, password , role})
        .then((res) => {
          resolve(res);
        })  
        .catch((err) => {
          reject(err);
        });
    });

    contact = (username, email, query) =>
    new Promise((resolve, reject) => {


      console.log(username)

      this.post("http://localhost:5000/contact", { username, email, query })
        .then((res) => {
          resolve(res);
        })  
        .catch((err) => {
          reject(err);
        });
    });

    save = (elements ,width , height, id) =>
    new Promise((resolve, reject) => {

      var data = JSON.stringify(elements);

      console.log(id)

      this.post("http://localhost:5000/save", { data ,width , height, id })
        .then((res) => {
          resolve(res);
        })  
        .catch((err) => {
          reject(err);
        });
    });

    change = (sub , role) =>
    new Promise((resolve, reject) => {

      console.log(role)

      this.post("http://localhost:5000/change", { sub , role })
        .then((res) => {
          resolve(res);
        })  
        .catch((err) => {
          reject(err);
        });
    });

    updatePass = (email, password, role) =>
    new Promise((resolve, reject) => {

      console.log(email)

      this.post("http://localhost:5000/updatePass", { email, password , role })
        .then((res) => {
          resolve(res);
        })  
        .catch((err) => {
          reject(err);
        });
    });

    isLoggedin = localStorage.getItem("token") ? true : false;

  logout = () => {
    localStorage.removeItem("token");
    window.location.href = "/";
  };

  getLoggedinfo = () => {
    try {
      let jwt = localStorage.getItem("token");
      var decode = jwt_decode(jwt);
      return decode;
    } catch (error) {
      console.log("error");
    }
  };

  isAdmin = () => {
    if (this.isLoggedIn) {
      if (this.getLoggedinfo().role === "admin") return true;
      else return false;
    } else return false;
  };
}

let UserServices = new userServices();
export default UserServices;
