import './css/App.css';
import {makeStyles} from "@material-ui/core/styles";
import home1 from "./img/home1.png";
import home2 from "./img/home2.png";
import home3 from "./img/home3.png";
import React, { useEffect } from 'react';

import HeartOutlined from "@ant-design/icons";
import {Button, IconButton} from "@material-ui/core";
import {BrowserRouter as Router, Link, Route, Switch} from "react-router-dom";
import MyEstimates from "./sketch";
import logo from "./img/logoA.png";
import {Col, Container, Row} from "react-bootstrap";


const useStyles = makeStyles((theme) => ({
    p: {
            width: 300,
            // height: 1476,
            overflow: "visible",
            letterSpacing: 0,
            color: "#433E69",
            fontSize: 20,
            fontWeight: 800,
            fontStyle: "italic",
            fontFamily: `"Inter-ExtraBoldItalic", "Inter", sans-serif`,
            lineHeight: 0.3,
            textAlign: "right",
        },
    frame : {
       marginLeft : "17%",
        // width: 600,
        // height: 500,
    },



}));

const Sketch = () => <div> <MyEstimates/>     </div>;

function Home() {

    const forceUpdate = React.useReducer(bool => !bool)[1];

    // forceUpdate();


    const myClass = useStyles();

    return (
        <Container fluid>


<Row>
            <Col md = {8} style={{marginTop : 100}}>
                <Col md = {2} style={{marginTop : -50}}>
                <img className={myClass.frame}
                     src = {home1}
                     alt="image" />
            </Col>
    <Col md = {2} style={{textAlign : 'right' , marginTop : -300}}>

                <img className={myClass.frame}
                     width={"35%"}
                     src = {home2}
                     alt="image" />
    </Col>
                <Col md = {4} style={{marginLeft : "45%" , marginTop : "-19%"}}>
                <p className={myClass.p} >
                    Sketch with app estimate with </p>

                         <Link to="/sketch" style={{textDecoration: 'none' , marginLeft : "-12%" , marginTop : "-2%" }} >
                             <IconButton>
                                 <img src={logo}
                                      width = "50%"
                                      height = "50%"
                                      alt="logo" />
                             </IconButton>
                         </Link>

                     </Col>


                <p className={myClass.p} style={{marginLeft : "41%" , width : 400}}>
                    Just Two Steps For Your Results </p>
                <p className={myClass.p} style={{marginLeft : "30%" , marginTop : "2.6%"}} >
                    Layout Design </p>
                <p className={myClass.p} style={{marginLeft : "55%" ,marginTop : "-1.4%"}}>
                    Estimate Design </p>
            </Col>

</Row><Row>

                <Col md = {8} style={{marginTop : "-20.5%"}}>

                <p  className={myClass.p} >
                Here you can</p>
    <p className={myClass.p} >
                build your </p>
    <p className={myClass.p}>
                buildings (</p>
    <p className={myClass.p}>
                architectural</p>
    <p className={myClass.p}>
                designs layout)</p>
    <p className={myClass.p}>
                and estimate</p>
    <p className={myClass.p}>
            the results. You</p>
    <p className={myClass.p}>
            can also see the</p>
    <p className={myClass.p}>
                    trending </p>
    <p className={myClass.p}>
                    materials in</p>
    <p className={myClass.p}>
                market and see</p>
    <p className={myClass.p}>
                their prices.</p>
</Col>


           <h1 style={{color : '#000000'}}> <HeartOutlined
            // size={500}
            // spin={true}
            //  // Using default values
            // theme={"outlined"}
            // type={"heart"}
            // fontsize = {100}
            //
                style={{ fontSize: '82px', color: '#08c' }}    />
           </h1>


            <Router>

                <Switch>
                    <Route path="/sketch" component={Sketch} />
                </Switch>
            </Router>

            <img className={myClass.frame}
                 style={{ marginLeft : "61%" , marginTop : "2%" }}
                 width="20%"
                 height="20%"
                 src = {home3}
                 alt="image" />

</Row>
        </Container>
    )
}
export default Home;
