import {useLayout} from 'react';
import $ from 'jquery';

export default function Nav () {

        var str = '#len'; //increment by 1 up to 1-nelemnts
    useLayout (()=>{
          var i, stop;
          i = 1;
          stop = 4; //num elements
          setInterval(function(){
            if (i > stop){
              return;
            }
            $('#len'+(i++)).toggleClass('bounce');
          }, 500)
        });


