import './css/App.css';
import {makeStyles} from "@material-ui/core/styles";
import front from './img/front.jpg';
import home1 from "./img/home1.png";
import home2 from "./img/home2.png";
import home3 from "./img/home3.png";
import home4 from "./img/home4.jpg";
import coffee from "./img/coffee.jpg"
import React, { useEffect } from 'react';
import { Spin } from 'antd';
import 'antd/dist/antd.css';
import {Button, IconButton} from "@material-ui/core";
import {BrowserRouter as Router, Link, Route, Switch} from "react-router-dom";
import MyEstimates from "./sketch";
import logo from "./img/logoA.png";
import 'bootstrap/dist/css/bootstrap.min.css';

import AnimatedButton from '@bit/tabinda.react-button-animation.animated.button';


const useStyles = makeStyles((theme) => ({
    p: {
            // width: 'auto',
            // height: 1476,
            overflow: "visible",
            letterSpacing: 0,
            color: "#433E69",
            fontSize: 25,
            fontWeight: 800,
            fontStyle: "italic",
            fontFamily: `"Inter-ExtraBoldItalic", "Inter", sans-serif`,
            lineHeight: 0.3,
            textAlign: "center",
            top: '20%' ,
            padding : '5px',
            transform: "translate('-50%' , -'50%' )" ,
            height: 'auto',
            // background: "radial-gradient(92.10000000000001% 50% at 5.2% 52.7%, #2266ff 0%, hsl(222, 57%, 77%) 100%)",
            // boirderRadius: '10',
        },
    frame : {
        width: '100%',
        height: 'auto',
    },

    textframe:{
    marginLeft : '30%',
    marginTop : '6%',
    width: '50%',
  height: 'auto',
  overflow: "hidden",
  transform: "translate('-50%' , -'50%' )" ,
  background: "radial-gradient(92.10000000000001% 50% at 5.2% 52.7%, #2266ff 0%, hsl(222, 57%, 77%) 100%)",
borderRadius: "5%",   

},

    myStyle : {
        position: 'absolute',
        top: '20%' ,
        left: '35%' ,
        transform: "translate('-50%' , -'50%' )" ,
        width: '30%',
        height: 'auto',
    }



}));

const Sketch = () => <div> <MyEstimates/>     </div>;

function Home() {

    const width = '100%';
    const height = 'auto';

    const myClass = useStyles();

    return (
<>
<img width = {width} height = {height}
                     src = {front}
                     alt="image" 
                     style = {{  opacity:0.8 , borderBottomRightRadius : '20%' }}

                     />


                     <img src = {logo} className={myClass.myStyle}/>

<div className="row" style={{marginTop:40}}>
  <div className="col-md-5">
      <div className={myClass.textframe}>
    <p  className={myClass.p} style={{paddingTop:20}}>
                 Here you can</p>
    <p className={myClass.p} >
                build your </p>
    <p className={myClass.p}>
                buildings (</p>
    <p className={myClass.p}>
                architectural</p>
   <p className={myClass.p}>
                designs layout)</p>
   <p className={myClass.p}>
                and estimate</p>
    <p className={myClass.p}>
          the results. You</p>
    <p className={myClass.p}>
            can also see the</p>
    <p className={myClass.p}>
                    trending </p>
    <p className={myClass.p}>
                    materials in</p>
    <p className={myClass.p}>
                market and see</p>
    <p className={myClass.p}>
                their prices.</p>
                </div>
    </div>

    <div className="col-md-4" style = {{marignTop : '5%'}}>
    <img className={myClass.frame}
                     src = {home1}
                     alt="image" />
    </div>

<div className="col-md-3">

<p className={myClass.p} style ={{textAlign: "left" , marginTop: '20%' , fontSize : '15.5px'}}>
                     Sketch with app estimate with </p>
                     <div style={{marginTop: '10%'}}></div>
                         <Link to="/sketch" style={{textDecoration: 'none' }} >

                             <AnimatedButton style={{ marginTop: '20%' , align : 'center' }} label =    "Sketch App Estimate"/>

                         </Link>
<div style={{marginTop: '10%'}}></div>
                         </div>
{/* <div className="col-md-4">
    <img className={myClass.frame}
                     width={"35%"}
                     src = {home2}
                     alt="image" />
                     </div> */}

</div>

<div className="row" style={{marginTop: '3%'}}>
    <div className="col-md-12">

    <p className={myClass.p} >
    Just two Steps For your results</p>

    </div>


</div>

<div className="row" style={{marginTop : '3%'}}>
    <div className="col-md-5">

    <p className={myClass.p} >
                    Layout Design </p>
                    <img style={{width:'50%' , height:'auto' ,  marginLeft: '25%' 
  , marginTop : '3%'}}
                 src = {home4}
                 alt="image" />


    </div>

    <div className="col-md-2">

        <Spin style={{width:'100%' , marginTop: '20%' , marginBottom:'20%'}} />
    </div>

    <div className="col-md-5">

    <p className={myClass.p} style={{marginTop:'3%'}}>
    Estimate Design </p>

    <img style={{width:'60%' , height:'auto' , marginLeft: '25%' }}
                 src = {home3}
                 alt="image" />

    </div>


</div>
<div className="row" style={{marginTop : '3%'}}>
<div className="col-md-4">
<p className={myClass.p} 
style={{
    marginTop:'3%' , 
    fontStyle : 'normal' , 
    fontSize : '20px' ,
    textAlign : 'right'
    }}>
Buy us a cup of Coffee and get your Subscription today </p>

<img style={{width:'60%' , height:'auto' , marginLeft: '25%' , borderRadius : '20%' }}
                 src = {coffee}
                 alt="image" />

<p className={myClass.p} 
style={{
    marginTop:'3%' , 
    fontStyle : 'normal' , 
    // fontSize : '20px' ,
    }}>
As low as 1$ per subscription </p>
</div>
</div>

</>



//             <Router>

//                 <Switch>
//                     <Route path="/sketch" component={Sketch} />
//                 </Switch>
//             </Router>

    )
}
export default Home;
