import {elements, setElements} from "../sketch";
import {createElement} from "./createElement";
import {widthh, heightt} from "../sketch";

export const updateElement = (id, x1 , y1 , x2 , y2 , element) => {

    if(x2-x1 < 0 && y2-y1 < 0 && x2-x1 >widthh && y2-y1 > heightt)
    {
        alert("Element must inside Canvas")
    }
    else{
    const updatedElement = createElement(id, x1 , y1 , x2 , y2 , element);
    const elementsCopy = [...elements];
    elementsCopy[id] = updatedElement;
    setElements(elementsCopy);
    }
};
