import React, { Component } from 'react';
import axios from 'axios';

class Create extends Component {
    constructor(props) {
        super(props);

        this.state = {
            username: '',
            password: '',
            data:'',
        };
    }

    handleInputChange = e => {
        this.setState({
            [e.target.name]: e.target.value,
        });
    };

    handleSubmit = e => {
        e.preventDefault();

        // console.log("happy")
        const { username , password } = this.state;


        const account = {
            username,
            password,
        };

        // console.log(account);



        axios.post('http://localhost:5000/auth', account )
            .then((res) =>{ 
                            // this.data = res.data
                            console.log(res.status);
                            res.isLoggedin = false;
                if(res.isLoggedin){

                    window.location.href = '/welcome ';
                }


                        })
            .catch(err => {
                console.error(err.status);  
            },{
                withCredentials: true,
                headers: {'Access-Control-Allow-Origin': '*', 'Content-Type': 'application/json'
            }});

        this.setState({ name: '', email: '' })

        document.forms['myform'].reset();


    };



    render() {
        return (
            <div>
                <br />
                <div className="container">

                        <div className="login-form">
                            <h1>Login Form</h1>
                            <form onSubmit={this.handleSubmit} id="myform">
                                <input type="text" name="username" placeholder="Username" required onChange={this.handleInputChange}/>
                                    <input type="password" name="password" placeholder="Password" required onChange={this.handleInputChange}/>
                                        <input type="submit"/>
                            </form>
                        </div>
                </div>
                <h1>{this.data}</h1>
            </div>
        );
    }
}

export default Create;
