import { toast } from "react-toastify";
import userServices from "../services/UserManagementServices";

const UserRemove = (userID) => async (dispatch) => {
  try {
    dispatch({ type: "USER_DELETE_REQUEST", payload: userID });
    const user = await userServices
      .deleteUser(userID)
      .catch((err) =>
        toast.error(err.response.data, {
          position: "bottom-center",
          autoClose: 3000,
          hideProgressBar: true,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
        })
      );

    dispatch({ type: "USER_DELETE_SUCCESS", payload: user, success: true });
  } catch (error) {
    dispatch({ type: "USER_DELETE_FAIL", payload: error });
  }
};

export default UserRemove;
