import {    mouse ,
            setMyArr , 
            elements , 
            setElements ,
            getElementAtPosition , 
            setAction , 
            setSelectedElement, 
            tool } 

            from "../sketch";


import {createElement} from "./createElement";

export const handleMouseDown = (event) => {
    // const { clientX , clientY} = event;
    if (tool === "selection") {
        const element = getElementAtPosition(mouse.x, mouse.y, elements);
        if(element)
        {
            const offsetX = mouse.x - element.x1 ;
            const offsetY = mouse.y - element.y1 ;

            setSelectedElement({...element,offsetX,offsetY});
            setAction("moving");
        }

    }
    else if(tool === "eraser")
    {
        const element = getElementAtPosition(mouse.x, mouse.y, elements);
        if(element)
        {
            const offsetX = mouse.x - element.x1 ;
            const offsetY = mouse.y - element.y1 ;
            setSelectedElement({...element,offsetX,offsetY});
            setAction("eraser");
        }
    }

    else if(tool === "text")
    {
        const element = getElementAtPosition(mouse.x, mouse.y, elements);
        if(element)
        {
            const offsetX = mouse.x - element.x1 ;
            const offsetY = mouse.y - element.y1 ;
            setSelectedElement({...element,offsetX,offsetY});
            setAction("text");
        }
    }
    else if(tool === "resize")
    {
        const element = getElementAtPosition(mouse.x, mouse.y, elements);
        if(element)
        {
            const offsetX = mouse.x - element.x1 ;
            const offsetY = mouse.y - element.y1 ;
            setSelectedElement({...element,offsetX,offsetY});
            setAction("moving");
        }
    }

    else if(tool === "sizes")
    {
        const element = getElementAtPosition(mouse.x, mouse.y, elements);
        if(element)
        {
            const offsetX = mouse.x - element.x1 ;
            const offsetY = mouse.y - element.y1 ;
            setSelectedElement({...element,offsetX,offsetY});
            let myJs = JSON.stringify(elements);
            setMyArr(JSON.parse(myJs))
            console.log( myArr );
        }
    }

    else {
        setAction("drawing");
        // const {clientX, clientY} = event;
        const id = elements.length;
        const element =  createElement(id, mouse.x, mouse.y, mouse.x, mouse.y, tool);
        setElements(prevState => [...prevState, element]);


    }
};
